var CrudCreate = {
	name : 'create', 
	urlParam : '/create/', 
	accessLogMsg : 'Tentativa de acesso a criação de entidade ',
	checkerName : 'checkCreate'
}

var CrudList = {
	name : 'list', 
	urlParam : '/list/', 
	accessLogMsg : 'Tentativa de acesso a listagem de entidade ',
	checkerName : 'checkList'
}

var CrudUpdate = {
	name : 'update', 
	urlParam : '/update/:id', 
	accessLogMsg : 'Tentativa de acesso a atualização de entidade ',
	checkerName : 'checkUpdate'
}

var CrudListOne = {
	name : 'listone', 
	urlParam : '/:id', 
	accessLogMsg : 'Tentativa de acesso a listagem única de entidade ',
	checkerName : 'checkListOne'
}

var CrudDelete = {
	name : 'delete', 
	urlParam : '/delete/:id', 
	accessLogMsg : 'Tentativa de acesso a remoção de entidade ',
	checkerName : 'checkDelete'
}

module.exports = {
	getCrudStrut : function(crudKind) {
		if (crudKind == 'create')
			return CrudCreate
		if (crudKind == 'update')
			return CrudUpdate
		if (crudKind == 'delete')
			return CrudDelete
		if (crudKind == 'list')
			return CrudList
		if (crudKind == 'listone')
			return CrudListOne

		return undefined
	}
}
