var global = { }

module.exports = {
	makeGlobalFollower : function(name) {
		global.follower = name
	},
	getGlobalCurrent : function() {
		if (global == undefined) 
			return undefined

		return global.follower
	},
	makeObjFollower : function(obj, name) {
		obj = (obj == null) ? { } : obj
		obj.follower = name
	},
	removeFollower : function(obj) {
		delete obj.follower
	},
	getCurrent : function(obj) {
		if (obj == undefined) 
			return undefined

		return obj.follower
	}
}
