var mongoose = require('mongoose')

function getSchema(schema) {
	var schemaModule = require.main.require('./schema/' + schema);
	return schemaModule.getSchema(mongoose);
}

module.exports = {
	setDatabase : function(db) {
		mongoose.connect('mongodb://localhost/' + db)
	},
	getModel : function(model) {
		return mongoose.model(model, getSchema(model))
	}
}
